
import { Card, CardContent } from "@/components/ui/card";

const Testimonials = () => {
  const testimonials = [
    {
      name: "Oluwaseun Adegoke",
      role: "Buka Owner, Surulere, Lagos",
      content:
        "CleanNigeria don solve our waste problem for my restaurant! Before, we dey struggle with waste, but now the collectors dey always come on time. The app na correct investment!",
      image: "data:image/jpeg;base64,",
      rating: 5,
    },
    {
      name: "Amina Ibrahim",
      role: "School Administrator, Wuse, Abuja",
      content:
        "Our school compound is now always clean thanks to CleanNigeria. The waste collectors are punctual and professional. The students are learning the importance of proper waste management too!",
      image: "data:image/jpeg;base64,",
      rating: 5,
    },
    {
      name: "Chidinma Eze",
      role: "Market Trader, Ariaria Market, Aba",
      content:
        "For our busy market, waste management na big challenge. Since we start to use CleanNigeria, the difference clear well-well! The area clean and customers happy to shop here.",
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTMojWrye5GWfuNnd-LSkwEMTBOHTqL5eGCJA&s",
      rating: 4,
    },
  ];

  return (
    <section className="py-16">
      <div className="container mx-auto px-4">
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold mb-4">What Our Users Say</h2>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Don't just take our word for it. Here's what people across Nigeria are saying about our waste collection service.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {testimonials.map((testimonial, index) => (
            <Card key={index} className="overflow-hidden border-none shadow-lg">
              <CardContent className="p-8 bg-gradient-to-br from-white to-gray-50">
                <div className="flex items-center mb-6">
                  <img
                    src={testimonial.image}
                    alt={testimonial.name}
                    className="w-14 h-14 rounded-full object-cover mr-4"
                  />
                  <div>
                    <h4 className="font-bold">{testimonial.name}</h4>
                    <p className="text-gray-600 text-sm">{testimonial.role}</p>
                  </div>
                </div>

                <div className="mb-4 flex">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <svg
                      key={i}
                      xmlns="http://www.w3.org/2000/svg"
                      viewBox="0 0 24 24"
                      fill={i < testimonial.rating ? "#FCD34D" : "none"}
                      stroke={i < testimonial.rating ? "#FCD34D" : "currentColor"}
                      className="w-5 h-5"
                    >
                      <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2" />
                    </svg>
                  ))}
                </div>

                <p className="text-gray-700 italic">"{testimonial.content}"</p>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
